import React, { useState, useEffect, useCallback } from "react";
import { FFmpeg } from "@ffmpeg/ffmpeg";
import { Link } from "react-router-dom";
import { fetchFile, toBlobURL } from "@ffmpeg/util";
import { getChatResponse } from "./lib/gemini";
import "./FileUpload.css";

const MAX_FILE_SIZE = 100 * 1024 * 1024; // 100MB
const ALLOWED_VIDEO_TYPES = ["video/mp4", "video/webm", "video/ogg"];

const FileUpload = () => {
  const [ffmpeg] = useState(() => new FFmpeg());
  const [file, setFile] = useState(null);
  const [audioUrl, setAudioUrl] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");
  const [showSuccess, setShowSuccess] = useState(false);
  const [ffmpegLoaded, setFfmpegLoaded] = useState(false);
  const [conversionProgress, setConversionProgress] = useState(0);
  const [showChatbot, setShowChatbot] = useState(false);
  const [chatMessage, setChatMessage] = useState("");
  const [chatHistory, setChatHistory] = useState([]);
  const [processingStatus, setProcessingStatus] = useState("");
  const [isProcessing, setIsProcessing] = useState(false);

  useEffect(() => {
    const loadFFmpeg = async () => {
      try {
        await ffmpeg.load({
          coreURL: await toBlobURL(
            "https://unpkg.com/@ffmpeg/core@0.12.4/dist/umd/ffmpeg-core.js",
            "text/javascript"
          ),
          wasmURL: await toBlobURL(
            "https://unpkg.com/@ffmpeg/core@0.12.4/dist/umd/ffmpeg-core.wasm",
            "application/wasm"
          ),
        });
        setFfmpegLoaded(true);
      } catch (error) {
        console.error("Error loading FFmpeg:", error);
        setErrorMessage("Error loading audio processor. Please try again.");
      }
    };
    loadFFmpeg();
  }, [ffmpeg]);

  useEffect(() => {
    return () => {
      if (audioUrl) {
        URL.revokeObjectURL(audioUrl);
      }
    };
  }, [audioUrl]);

  const validateFile = useCallback((file) => {
    if (!file) {
      throw new Error("Please select a file to upload.");
    }

    if (!ALLOWED_VIDEO_TYPES.includes(file.type)) {
      throw new Error("Please select a valid video file (MP4, WebM, or OGG).");
    }

    if (file.size > MAX_FILE_SIZE) {
      throw new Error("File size exceeds 100MB limit.");
    }
  }, []);

  const handleFileChange = useCallback(
    (event) => {
      const selectedFile = event.target.files?.[0];
      if (!selectedFile) return;

      try {
        validateFile(selectedFile);
        setFile(selectedFile);
        setErrorMessage("");
        setAudioUrl(null);
        setShowSuccess(false);
        setConversionProgress(0);
        setShowChatbot(false);
        setChatHistory([]);
      } catch (error) {
        setErrorMessage(error.message);
        setFile(null);
      }
    },
    [validateFile]
  );

  const handleChatSubmit = async (e) => {
    e.preventDefault();
    if (!chatMessage.trim() || isProcessing) return;

    const userMessage = chatMessage.trim();
    setChatMessage("");
    setIsProcessing(true);

    try {
      // Add user message to chat history
      setChatHistory((prev) => [
        ...prev,
        { role: "user", content: userMessage },
      ]);

      // Get response from Gemini AI
      const response = await getChatResponse(chatHistory, userMessage);

      // Add AI response to chat history
      setChatHistory((prev) => [...prev, { role: "bot", content: response }]);
    } catch (error) {
      console.error("Chat Error:", error);
      setChatHistory((prev) => [
        ...prev,
        {
          role: "bot",
          content: "Sorry, I encountered an error. Please try again.",
        },
      ]);
    } finally {
      setIsProcessing(false);
    }
  };

  const extractAudio = async (videoFile) => {
    try {
      console.log("Starting audio extraction from video:", {
        fileName: videoFile.name,
        fileType: videoFile.type,
        fileSize: videoFile.size,
      });

      await ffmpeg.writeFile("input.mp4", await fetchFile(videoFile));
      console.log("Video file written successfully");

      ffmpeg.on("progress", ({ progress }) => {
        console.log("Extraction progress:", Math.round(progress * 100));
        setConversionProgress(Math.round(progress * 100));
      });

      await ffmpeg.exec([
        "-i",
        "input.mp4",
        "-vn",
        "-acodec",
        "libmp3lame",
        "-ab",
        "128k",
        "-ar",
        "44100",
        "-y",
        "output.mp3",
      ]);

      const data = await ffmpeg.readFile("output.mp3");
      const audioBlob = new Blob([data.buffer], { type: "audio/mpeg" });

      await ffmpeg.deleteFile("input.mp4");
      await ffmpeg.deleteFile("output.mp3");

      return audioBlob;
    } catch (error) {
      console.error("Audio extraction error:", error);
      throw new Error(`Failed to extract audio: ${error.message}`);
    }
  };

  const handleUpload = async () => {
    if (!file || !ffmpegLoaded) {
      setErrorMessage(
        "Please select a file and wait for the processor to load."
      );
      return;
    }

    try {
      setUploading(true);
      setErrorMessage("");
      setProcessingStatus("Extracting audio...");

      const audioBlob = await extractAudio(file);
      const url = URL.createObjectURL(audioBlob);
      setAudioUrl(url);

      setShowSuccess(true);
      setTimeout(() => setShowSuccess(false), 5000);

      // Initialize chatbot with a welcome message
      setShowChatbot(true);
      setChatHistory([
        {
          role: "bot",
          content:
            "I'm ready to help you with any questions about your lecture. What would you like to know?",
        },
      ]);
    } catch (error) {
      console.error("Processing Error:", error);
      setErrorMessage(error.message);
    } finally {
      setUploading(false);
      setConversionProgress(0);
      setProcessingStatus("");
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center py-6">
          <Link
            to="/"
            className="text-4xl font-bold text-blue-600 hover:text-blue-700"
          >
            LectureEase
          </Link>
          <Link
            to="/"
            className="bg-white text-blue-600 hover:text-blue-700 font-semibold py-2 px-4 border border-blue-600 rounded-lg shadow-sm hover:shadow-md transition-all"
          >
            Sign out
          </Link>
        </div>

        <div className="mt-8 bg-white rounded-xl shadow-lg p-6">
          <h2 className="text-2xl font-semibold text-gray-800 mb-6 text-center">
            Upload Your Lecture Video
          </h2>

          <div className="space-y-4">
            <div className="flex items-center justify-center w-full">
              <label className="flex flex-col items-center justify-center w-full h-32 border-2 border-blue-300 border-dashed rounded-lg cursor-pointer bg-gray-50 hover:bg-gray-100">
                <div className="flex flex-col items-center justify-center pt-5 pb-6">
                  <svg
                    className="w-8 h-8 mb-4 text-blue-500"
                    fill="none"
                    stroke="currentColor"
                    viewBox="0 0 24 24"
                  >
                    <path
                      strokeLinecap="round"
                      strokeLinejoin="round"
                      strokeWidth="2"
                      d="M7 16a4 4 0 01-.88-7.903A5 5 0 1115.9 6L16 6a5 5 0 011 9.9M15 13l-3-3m0 0l-3 3m3-3v12"
                    />
                  </svg>
                  <p className="mb-2 text-sm text-gray-500">
                    <span className="font-semibold">Click to upload</span> or
                    drag and drop
                  </p>
                  <p className="text-xs text-gray-500">
                    MP4, WebM, or OGG (MAX. 100MB)
                  </p>
                </div>
                <input
                  type="file"
                  className="hidden"
                  onChange={handleFileChange}
                  accept={ALLOWED_VIDEO_TYPES.join(",")}
                  disabled={uploading}
                />
              </label>
            </div>

            <div className="flex justify-center">
              <button
                onClick={handleUpload}
                disabled={!file || uploading || !ffmpegLoaded}
                className={`px-6 py-2 rounded-lg font-semibold text-white transition-all ${
                  !file || uploading || !ffmpegLoaded
                    ? "bg-gray-400 cursor-not-allowed"
                    : "bg-blue-600 hover:bg-blue-700"
                }`}
              >
                {uploading ? "Processing..." : "Upload"}
              </button>
            </div>

            {processingStatus && (
              <div className="flex items-center justify-center space-x-2 text-gray-600">
                <div className="animate-spin rounded-full h-4 w-4 border-2 border-blue-600 border-t-transparent"></div>
                <span>{processingStatus}</span>
              </div>
            )}

            {conversionProgress > 0 && conversionProgress < 100 && (
              <div className="w-full">
                <div className="w-full bg-gray-200 rounded-full h-2.5">
                  <div
                    className="bg-blue-600 h-2.5 rounded-full transition-all duration-300"
                    style={{ width: `${conversionProgress}%` }}
                  ></div>
                </div>
                <p className="text-center text-sm text-gray-600 mt-2">
                  {uploading ? "Uploading" : "Converting"}: {conversionProgress}
                  %
                </p>
              </div>
            )}

            {showSuccess && (
              <div className="text-center text-green-600 font-semibold">
                Video successfully processed!
              </div>
            )}

            {errorMessage && (
              <div className="bg-red-50 border-l-4 border-red-500 p-4">
                <div className="flex">
                  <div className="flex-shrink-0">
                    <svg
                      className="h-5 w-5 text-red-400"
                      viewBox="0 0 20 20"
                      fill="currentColor"
                    >
                      <path
                        fillRule="evenodd"
                        d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z"
                        clipRule="evenodd"
                      />
                    </svg>
                  </div>
                  <div className="ml-3">
                    <p className="text-sm text-red-700">{errorMessage}</p>
                  </div>
                </div>
              </div>
            )}

            {showChatbot && (
              <div className="mt-8 border rounded-lg shadow-sm">
                <div className="h-96 overflow-y-auto p-4 space-y-4">
                  {chatHistory.map((message, index) => (
                    <div
                      key={index}
                      className={`flex ${
                        message.role === "user"
                          ? "justify-end"
                          : "justify-start"
                      }`}
                    >
                      <div
                        className={`max-w-[80%] rounded-lg px-4 py-2 ${
                          message.role === "user"
                            ? "bg-blue-600 text-white"
                            : "bg-gray-100 text-gray-800"
                        }`}
                      >
                        {message.content}
                      </div>
                    </div>
                  ))}
                </div>
                <form onSubmit={handleChatSubmit} className="border-t p-4">
                  <div className="flex space-x-2">
                    <input
                      type="text"
                      value={chatMessage}
                      onChange={(e) => setChatMessage(e.target.value)}
                      placeholder="Ask a question about your lecture..."
                      className="flex-1 rounded-lg border border-gray-300 px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                      disabled={isProcessing}
                    />
                    <button
                      type="submit"
                      disabled={isProcessing || !chatMessage.trim()}
                      className={`px-4 py-2 rounded-lg font-semibold text-white ${
                        isProcessing || !chatMessage.trim()
                          ? "bg-gray-400 cursor-not-allowed"
                          : "bg-blue-600 hover:bg-blue-700"
                      }`}
                    >
                      Send
                    </button>
                  </div>
                </form>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default FileUpload;
