import React, { useState } from "react";
import axios from "axios";
import "./FileUpload.css";

const MAX_FILE_SIZE = 100 * 1024 * 1024; // 100MB
const ALLOWED_VIDEO_TYPES = ["video/mp4", "video/webm", "video/ogg"];

const FileUpload = () => {
  const [file, setFile] = useState(null);
  const [audioUrl, setAudioUrl] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");
  const [showSuccess, setShowSuccess] = useState(false);
  const [conversionProgress, setConversionProgress] = useState(0);
  const [showChatbot, setShowChatbot] = useState(false);
  const [chatMessage, setChatMessage] = useState("");
  const [chatHistory, setChatHistory] = useState([]);
  const [processingStatus, setProcessingStatus] = useState("");

  const handleFileChange = (event) => {
    const selectedFile = event.target.files[0];
    if (selectedFile) {
      setFile(selectedFile);
      setErrorMessage("");
      setAudioUrl(null);
      setShowSuccess(false);
      setConversionProgress(0);
      setShowChatbot(false);
      setChatHistory([]);
    }
  };

  const handleUpload = async () => {
    try {
      if (!file) {
        throw new Error("Please select a file to upload.");
      }

      setUploading(true);
      setErrorMessage("");

      const formData = new FormData();
      formData.append("audio", file);

      // Upload and process the file
      setProcessingStatus("Processing...");
      const response = await axios.post(
        "http://localhost:5001/api/upload-and-summarize",
        formData,
        {
          headers: { "Content-Type": "multipart/form-data" },
          onUploadProgress: (progressEvent) => {
            const uploadProgress = Math.round(
              (progressEvent.loaded * 100) / progressEvent.total
            );
            setConversionProgress(uploadProgress);
          },
        }
      );

      // Handle success
      if (response.data.summary) {
        setAudioUrl(response.data.audioUrl);
        setShowSuccess(true);
        setTimeout(() => setShowSuccess(false), 5000);

        // Display the summary in the chatbot if available
        setShowChatbot(true);
        setChatHistory([
          { role: "bot", content: `Summary: ${response.data.summary}` },
        ]);
      }
    } catch (error) {
      setErrorMessage(error.message);
    } finally {
      setUploading(false);
      setConversionProgress(0);
      setProcessingStatus("");
    }
  };

  const handleChatSubmit = async (e) => {
    e.preventDefault();
    if (!chatMessage.trim()) return;

    const userMessage = chatMessage.trim();
    setChatHistory((prev) => [...prev, { role: "user", content: userMessage }]);
    setChatMessage("");

    try {
      const response = await axios.post("http://localhost:5001/api/chat", {
        userMessage,
      });

      setChatHistory((prev) => [
        ...prev,
        {
          role: "bot",
          content: response.data.reply || "No response received from Gemini.",
        },
      ]);
    } catch (error) {
      setChatHistory((prev) => [
        ...prev,
        { role: "bot", content: "Error occurred, please try again." },
      ]);
    }
  };

  return (
    <div className="home">
      <div className="container">
        <div className="lecture-summary">
          <h2>Lecture Summary</h2>
          <h3>Upload your video to get an AI summary!</h3>
        </div>

        <div className="file-upload-container">
          <input
            type="file"
            accept="video/mp4, video/webm, video/ogg"
            onChange={handleFileChange}
          />
          {errorMessage && <p className="error-message">{errorMessage}</p>}
          {file && (
            <button onClick={handleUpload} disabled={uploading}>
              {uploading ? "Uploading..." : "Upload and Summarize"}
            </button>
          )}
        </div>

        {processingStatus && (
          <div className="processing-status">
            <p>{processingStatus}</p>
            <progress value={conversionProgress} max="100" />
          </div>
        )}

        {showSuccess && (
          <div className="success-message">
            <p>Audio extracted successfully!</p>
          </div>
        )}

        {audioUrl && (
          <div className="audio-player">
            <audio controls>
              <source src={audioUrl} type="audio/mpeg" />
              Your browser does not support the audio element.
            </audio>
          </div>
        )}

        {showChatbot && (
          <div className="chatbot-container">
            <div className="chat-history">
              {chatHistory.map((msg, idx) => (
                <div key={idx} className={msg.role}>
                  <p>{msg.content}</p>
                </div>
              ))}
            </div>
            <form onSubmit={handleChatSubmit}>
              <input
                type="text"
                value={chatMessage}
                onChange={(e) => setChatMessage(e.target.value)}
                placeholder="Ask something..."
              />
              <button type="submit">Send</button>
            </form>
          </div>
        )}
      </div>
    </div>
  );
};

export default FileUpload;
